    <div>
      {/* Display a list of books */}
      {books.map((book) => (
        <div key={book.id}>
          <h3>{book.title}</h3>
          <p>{book.description}</p>
          <button onClick={() => addToCollection(book)}>Add to Collection</button>
        </div>
      ))}
    </div>
  );
}





export default MyBooks;
